import React, { useState } from 'react';
import { BookOpen, ChevronDown, ChevronRight, Leaf, Droplets, Sun, Thermometer, CloudRain } from 'lucide-react';
import { useLanguage } from '../contexts/LanguageContext';
import { translations } from '../translations/translations';
import './KnowledgeSidebar.css';

const KnowledgeSidebar = () => {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const knowledgeData = {
    soilBasics: {
      title: "Soil Basics",
      icon: <Leaf size={16} />,
      content: [
        "Soil pH affects nutrient availability",
        "Most crops prefer pH 6.0-7.5",
        "Test soil every 2-3 years",
        "Organic matter improves soil health"
      ]
    },
    nutrients: {
      title: "Essential Nutrients",
      icon: <Droplets size={16} />,
      content: [
        "Nitrogen (N): Leaf growth and green color",
        "Phosphorus (P): Root development and flowering",
        "Potassium (K): Disease resistance and fruit quality",
        "Micronutrients: Iron, Zinc, Manganese, etc."
      ]
    },
    climate: {
      title: "Climate Factors",
      icon: <Sun size={16} />,
      content: [
        "Temperature affects crop growth rate",
        "Humidity influences disease pressure",
        "Rainfall timing is crucial for yield",
        "Consider seasonal variations"
      ]
    },
    cropTypes: {
      title: "Crop Categories",
      icon: <Thermometer size={16} />,
      content: [
        "Cereals: Rice, Wheat, Maize",
        "Pulses: Lentils, Chickpeas, Beans",
        "Fruits: Mango, Banana, Apple",
        "Vegetables: Tomato, Onion, Potato"
      ]
    },
    tips: {
      title: "Farming Tips",
      icon: <CloudRain size={16} />,
      content: [
        "Rotate crops to maintain soil health",
        "Use organic fertilizers when possible",
        "Monitor weather forecasts regularly",
        "Keep records of yields and inputs"
      ]
    }
  };

  const cropInfo = {
    rice: { name: "Rice", desc: "Staple food, needs lots of water", conditions: "High humidity, warm temp" },
    maize: { name: "Maize", desc: "High yield cereal crop", conditions: "Moderate water, warm climate" },
    chickpea: { name: "Chickpea", desc: "Protein-rich pulse", conditions: "Cool climate, low water" },
    banana: { name: "Banana", desc: "Tropical fruit crop", conditions: "High temp, high humidity" },
    cotton: { name: "Cotton", desc: "Fiber crop", conditions: "Hot climate, moderate water" }
  };

  return (
    <div className="knowledge-sidebar">
      <div className="sidebar-header">
        <BookOpen size={20} />
        <h3>Agricultural Knowledge</h3>
      </div>

      <div className="knowledge-sections">
        {Object.entries(knowledgeData).map(([key, section]) => (
          <div key={key} className="knowledge-section">
            <div 
              className="section-header"
              onClick={() => toggleSection(key)}
            >
              {expandedSections[key] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
              {section.icon}
              <span>{section.title}</span>
            </div>
            {expandedSections[key] && (
              <div className="section-content">
                <ul>
                  {section.content.map((item, index) => (
                    <li key={index}>{item}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="crop-quick-info">
        <h4>Quick Crop Info</h4>
        <div className="crop-cards">
          {Object.entries(cropInfo).map(([key, crop]) => (
            <div key={key} className="crop-card">
              <div className="crop-name">{crop.name}</div>
              <div className="crop-desc">{crop.desc}</div>
              <div className="crop-conditions">{crop.conditions}</div>
            </div>
          ))}
        </div>
      </div>

      <div className="sidebar-footer">
        <p>ðŸ’¡ Tip: Use the chatbot for natural language queries!</p>
      </div>
    </div>
  );
};

export default KnowledgeSidebar;
