import React, { useState } from 'react';
import { Send, RotateCcw, Info } from 'lucide-react';
import './InputForm.css';

const InputForm = ({ onPrediction, loading, onReset }) => {
  const [formData, setFormData] = useState({
    N: '',
    P: '',
    K: '',
    temperature: '',
    humidity: '',
    ph: '',
    rainfall: ''
  });

  const [showInfo, setShowInfo] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validate form
    const values = Object.values(formData);
    if (values.some(value => value === '')) {
      alert('Please fill in all fields');
      return;
    }

    // Convert to numbers
    const numericData = {};
    Object.keys(formData).forEach(key => {
      numericData[key] = parseFloat(formData[key]);
    });

    onPrediction(numericData);
  };

  const handleReset = () => {
    setFormData({
      N: '',
      P: '',
      K: '',
      temperature: '',
      humidity: '',
      ph: '',
      rainfall: ''
    });
    onReset();
  };

  const inputFields = [
    {
      name: 'N',
      label: 'Nitrogen (N)',
      unit: 'kg/ha',
      description: 'Amount of nitrogen in soil',
      min: 0,
      max: 200,
      step: 1
    },
    {
      name: 'P',
      label: 'Phosphorus (P)',
      unit: 'kg/ha',
      description: 'Amount of phosphorus in soil',
      min: 0,
      max: 200,
      step: 1
    },
    {
      name: 'K',
      label: 'Potassium (K)',
      unit: 'kg/ha',
      description: 'Amount of potassium in soil',
      min: 0,
      max: 200,
      step: 1
    },
    {
      name: 'temperature',
      label: 'Temperature',
      unit: '°C',
      description: 'Average temperature',
      min: 0,
      max: 50,
      step: 0.1
    },
    {
      name: 'humidity',
      label: 'Humidity',
      unit: '%',
      description: 'Relative humidity',
      min: 0,
      max: 100,
      step: 1
    },
    {
      name: 'ph',
      label: 'pH Level',
      unit: '',
      description: 'Soil pH level',
      min: 0,
      max: 14,
      step: 0.1
    },
    {
      name: 'rainfall',
      label: 'Rainfall',
      unit: 'mm',
      description: 'Annual rainfall',
      min: 0,
      max: 500,
      step: 1
    }
  ];

  return (
    <div className="input-form">
      <div className="form-header">
        <h2>Soil & Climate Parameters</h2>
        <button 
          className="info-button"
          onClick={() => setShowInfo(!showInfo)}
          title="Parameter Information"
        >
          <Info size={20} />
        </button>
      </div>

      {showInfo && (
        <div className="info-panel">
          <h3>Parameter Guidelines:</h3>
          <ul>
            <li><strong>N, P, K:</strong> Soil nutrient levels in kg per hectare</li>
            <li><strong>Temperature:</strong> Average temperature in Celsius</li>
            <li><strong>Humidity:</strong> Relative humidity percentage</li>
            <li><strong>pH:</strong> Soil acidity/alkalinity (0-14 scale)</li>
            <li><strong>Rainfall:</strong> Annual rainfall in millimeters</li>
          </ul>
        </div>
      )}

      <form onSubmit={handleSubmit} className="form">
        <div className="form-grid">
          {inputFields.map(field => (
            <div key={field.name} className="input-group">
              <label htmlFor={field.name} className="input-label">
                {field.label}
                <span className="unit">({field.unit})</span>
              </label>
              <input
                type="number"
                id={field.name}
                name={field.name}
                value={formData[field.name]}
                onChange={handleInputChange}
                min={field.min}
                max={field.max}
                step={field.step}
                className="input-field"
                placeholder={`Enter ${field.label.toLowerCase()}`}
                title={field.description}
              />
            </div>
          ))}
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={handleReset}
            className="reset-button"
            disabled={loading}
          >
            <RotateCcw size={18} />
            Reset
          </button>
          <button
            type="submit"
            className="submit-button"
            disabled={loading}
          >
            {loading ? (
              <>
                <div className="spinner"></div>
                Analyzing...
              </>
            ) : (
              <>
                <Send size={18} />
                Get Recommendation
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default InputForm;
